--drop table malnutrition_estimates;
SELECT DISTINCT country 
FROM malnutrition_estimates;
/*Countries and their level of income earners*/
SELECT 
    CASE
        WHEN income_classification = 3 THEN 'High income earning country'
        WHEN income_classification = 2 THEN 'Upper Middle Income earning country'
        WHEN income_classification = 1 THEN 'Lower Middle Income earning country'
        ELSE 'Low income earning country'
    END AS Income_classification_identifier,    
income_classification,country
FROM malnutrition_estimates
GROUP BY country,income_classification
ORDER BY 2 DESC;

/* high income earners between 2004 and 2018*/
SELECT country AS high_income_earning_countries,income_classification
FROM malnutrition_estimates
WHERE (year BETWEEN 2004 AND 2018) AND (income_classification = 3)
GROUP BY country,income_classification;

/*low income earners between 2004 and 2018*/
SELECT country AS low_income_earning_countries,income_classification
FROM malnutrition_estimates
WHERE (year BETWEEN 2004 AND 2018) AND (income_classification = 0)
GROUP BY country,income_classification;

/*least developed countries*/
SELECT country,LDC AS least_developed_countries
FROM malnutrition_estimates
WHERE LDC = 1
GROUP BY country,LDC;
/* countries with low income food deficient*/
SELECT country,LIFD AS Low_Income_Food_Deficient
FROM malnutrition_estimates
WHERE LIFD = 1
GROUP BY country,LIFD;
/*least developed countries with low income food deficient*/
SELECT country,LIFD AS Low_Income_Food_Deficient,LDC AS least_developed_countries
FROM malnutrition_estimates
WHERE LIFD = 1 AND LDC =1
GROUP BY country,LIFD,LDC;

/*landlocked developing countries*/
SELECT country,LLDC_SID2 AS landlocked_countries
FROM malnutrition_estimates
WHERE LLDC_SID2 = 1
GROUP BY country,LLDC_SID2;

/*small island  developing states*/
SELECT country,LLDC_SID2 AS small_islands
FROM malnutrition_estimates
WHERE LLDC_SID2 = 2
GROUP BY country,LLDC_SID2;
/*average wasting*/
SELECT AVG(wasting) AS average_wasting
FROM malnutrition_estimates;
/*maximum wasting*/
SELECT MAX(wasting) as maximum_wasting
FROM malnutrition_estimates;

/*average stunting*/
SELECT AVG(stunting) AS average_stunting
FROM malnutrition_estimates;
/*maximum stunting*/
SELECT MAX(stunting) as maximum_stuntiing
FROM malnutrition_estimates;

/*severe wasting countries*/
SELECT country,severe_wasting,year
FROM malnutrition_estimates
WHERE severe_wasting IS NOT NULL
GROUP BY country,severe_wasting,year
ORDER BY 2 DESC;
/*source of data*/
SELECT country,source
FROM malnutrition_estimates
GROUP BY source,country;




    








